class ManifestValidator {
  constructor(manifest) { this.manifest = manifest; }

  validateStructure(filesPresent) {
    const required = this.manifest._executable_mechanics.validation_schema.required_files;
    const missing = required.filter(f => !filesPresent.includes(f));
    return { valid: missing.length === 0, missing };
  }

  checkDependencies(file, loaded) {
    const deps = this.manifest._executable_mechanics.dependency_graph[file] || [];
    return deps.every(d => loaded.includes(d));
  }
}
